import Boton from "./Boton"
import Country from "./Country"

const Countries = ({countries, filter}) => {
    let filterCountry = countries.filter((country) => country.name.common.toLowerCase().includes(filter.toLowerCase())) //country.name.common.toLowerCase().indexOf(filter.toLowerCase()) !== -1)
    // const handleClick = (country) =>{return <Country key={country.name.common} country={country} /> }
    filterCountry.length > 10 ? <p>Too many matches, specify another filter</p> :
    filterCountry.length > 1 ? filterCountry.map((filteredCountry, i) => {return <li key={i}>{filteredCountry.name.common}<Boton text={"show"} handleClick={()=>handleClick(filteredCountry)}/></li>}) :
    filterCountry.map((filteredCountry, i) => {return <Country key={i} country={filteredCountry} />})

}

export default Countries
