import Boton from "./Boton"
import Country from "./Country"

const Countries = ({countries, setCountries}) => {
    // let filterCountry = countries.filter((country) => country.name.common.toLowerCase().includes(setCountries.toLowerCase())) //country.name.common.toLowerCase().indexOf(filter.toLowerCase()) !== -1)
    const handleClick = (country) => {setCountries([country])}
    return(
        countries.length > 10 ? <p>Too many matches, specify another filter</p> :
        countries.length > 1 ? countries.map((filteredCountry) => <li key={filteredCountry.name.common}>{filteredCountry.name.common}<Boton text={"show"} handleClick={()=>handleClick(filteredCountry)}/></li>) :
        countries.map((filteredCountry) => <Country key={filteredCountry.name.common} country={filteredCountry} />)
    )
}

export default Countries
